mapper.select:
select

from user
where us_phone = #{usPhone,jdbcType=VARCHAR}
and us_password =
#{usPassword,jdbcType=VARCHAR}

mapper.update:
update user set
us_subm_img_sign=1, us_checkpass_sign = 0 where
us_id=#{usId,jdbcType=VARCHAR}

mapper.select:
select

from user
where us_subm_img_sign = 0 and us_id=#{usId,jdbcType=VARCHAR}
